import './App.css';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import About from './components/About';
import React,{useState} from 'react';
// import TodoForm from './components/TodoForm';
import {
  BrowserRouter,
  Route,
  Routes,
} from "react-router-dom";

function App() {
  const [mode, setMode ] = useState('light');

  const toggleMode = ()=>{
    if(mode === 'light'){
       setMode ('dark')
       document.body.style.backgroundColor = 'black'

    }

    else{
      setMode ('light')
      document.body.style.backgroundColor = 'white'

    }
  }
  return (
<>
  <Navbar title="textutils"  mode={mode} toggleMode={toggleMode}/>

  <div className='container my-3' >
  {/* <TextForm heading="Enter text to analyze" upper="Convert to Uppercase" mode={mode}/> */}

      <BrowserRouter>
        <Routes>
          <Route exact path="/about" element={<About mode={mode} aboutText="hi"/>}></Route>
          <Route exact path="/" element={<TextForm heading="Enter text to analyze" upper="Convert to Uppercase" mode={mode}/>}/>
        </Routes>
    </BrowserRouter>
  </div>

</>
  )
};

export default App;
